def cls_myVals(arr_size, sortVal, speedVal, userArr):
    myVals.getVal(arr_size, sortVal, speedVal, userArr)
    a = myVals.userArr.split()
    a = list(map(int, a))
    return arr_size, sortVal, speedVal, a


def random_arr(arr_size, sortVal, speedVal, userArr):
    myVals.getVal(arr_size, sortVal, speedVal, userArr)
    arr = np.round(np.linspace(0, 1000, int(myVals.N)), 0)
    a = list(map(int, arr))
    np.random.seed(0)
    np.random.shuffle(a)
    return a


def make_grid(width, rows):
    gap = width // rows
    offset = 2
    for i in range(rows):
        grid_rows.append([])
        grid_cols.append([])
        for j in range(rows):
            grid_rows[i].append(i)
            grid_cols[i].append(j)
    return grid_rows, grid_cols, gap


def test_run1():
    assert cls_myVals(10, 2, 3, "10 20 30") == (10, 2, 3, [10, 20, 30])


def test_run2():
    assert random_arr(5, 2, 3, "5 10 15 20 25") == [500, 0, 250, 750, 1000]


def test_run3():
    assert random_arr(10, 2, 3, "2") == [222, 889, 444, 1000, 111, 667, 778, 333, 0, 556]


def test_run4():
    assert make_grid(600, 25) == (ROWS, COLS, 24)
